import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./Navbar.css";
import { Button } from "./Button";

function Navbar() {
  const [click, setClick] = useState(false);
  const [button, setButton] = useState(true);

  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

  const showButton = () => {
    if (window.innerWidth <= 1200) {
      setButton(false);
    } else {
      setButton(true);
    }
  };

  useEffect(() => {
    showButton();
  }, []);

  window.addEventListener("resize", showButton);

  return (
    <>
      <nav className="navbar">
        <div className="navbar-container">
          GUMISERVIS GS <i className="fas fa-car" />
          <Link to="/" className="navbar-logo" onClick={closeMobileMenu}></Link>
          <div className="menu-icon" onClick={handleClick}>
            <i className={click ? "fas fa-times" : "fas fa-bars"} />
          </div>
          <ul className={click ? "nav-menu active" : "nav-menu"}>
            <li className="nav-item">
              <Link to="/" className="nav-links" onClick={closeMobileMenu}>
                Početna
              </Link>
            </li>

            <li className="nav-item">
              <Link to="/offer" className="nav-links" onClick={closeMobileMenu}>
                Ponuda
              </Link>
            </li>

            <li className="nav-item">
              <Link
                to="/appointment"
                className="nav-links"
                onClick={closeMobileMenu}
              >
                Naruči se
              </Link>
            </li>

            <li className="nav-item">
              <Link
                to="/notification"
                className="nav-links"
                onClick={closeMobileMenu}
              >
                Obavijesti me
              </Link>
            </li>

            <li className="nav-item">
              <Link to="/help" className="nav-links" onClick={closeMobileMenu}>
                Pomoć
              </Link>
            </li>

            <li className="nav-item">
              <Link
                to="/contact"
                className="nav-links"
                onClick={closeMobileMenu}
              >
                Kontakt
              </Link>
            </li>

            <li>
              <Link
                to="/login"
                className="nav-links-mobile"
                onClick={closeMobileMenu}
              >
                Prijavi se
              </Link>
            </li>
          </ul>
          {button && (
            <Button linkon="1" linkpath="/Login" buttonstyle="btn--outline">
              Prijavi se
            </Button>
          )}
        </div>
      </nav>
    </>
  );
}

export default Navbar;
