import React, { useState } from "react";
import "../../App.css";
import Axios from "axios";
import TextField from "@material-ui/core/TextField";
import TextareaAutosize from "@material-ui/core/TextareaAutosize";
import Checkbox from "@material-ui/core/Checkbox";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import { Button } from "@material-ui/core";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import FormHelperText from "@material-ui/core/FormHelperText";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";
import FormGroup from "@material-ui/core/FormGroup";

//create your forceUpdate hook
function useForceUpdate() {
  const [value, setValue] = useState(0); // integer state
  return () => setValue((value) => value + 1); // update the state to force render
}

export default function Appointment() {
  const [errorName, setErrorName] = useState(false);
  const [errorSurname, setErrorSurname] = useState(false);
  const [errorEmail, setErrorEmail] = useState(false);
  const [errorPhoneNo, setErrorPhoneNo] = useState(false);
  const [errorOfferCode, setErrorOfferCode] = useState(false);
  const [errorQuantity, setErrorQuantity] = useState(false);
  const [errorTyreWidth, setErrorTyreWidth] = useState(false);
  const [errorTyreHeight, setErrorTyreHeight] = useState(false);
  const [errorTyreDiameter, setErrorTyreDiameter] = useState(false);
  const [errorTermsConditions, setErrorTermsConditions] = useState(false);

  const [name, setName] = useState("");
  const [surname, setSurname] = useState("");
  const [Email, setEmail] = useState("");
  const [phoneNo, setPhoneNo] = useState("");
  const [offerCode, setOfferCode] = useState("");
  const [quantity, setQuantity] = useState("");
  const [tyreWidth, setTyreWidth] = useState(0);
  const [tyreHeight, setTyreHeight] = useState(0);
  const [tyreDiameter, setTyreDiameter] = useState(0);
  const [comment, setComment] = useState("");

  function checkNameSurname(str) {
    if (str.length === 0) {
      return false;
    }

    for (let i = 0; i < str.length; ++i) {
      if (
        !(
          (str.charCodeAt(i) >= 65 && str.charCodeAt(i) <= 90) ||
          (str.charCodeAt(i) >= 97 && str.charCodeAt(i) <= 122) ||
          str.charAt(i) === "š" ||
          str.charAt(i) === "đ" ||
          str.charAt(i) === "č" ||
          str.charAt(i) === "ć" ||
          str.charAt(i) === "ž" ||
          str.charAt(i) === " "
        )
      ) {
        return false;
      }
    }

    return true;
  }

  function checkPhoneQuantity(str, isPhone) {
    if (str.length === 0) {
      return false;
    }

    for (let i = 0; i < str.length; ++i) {
      if (
        !(
          (str.charCodeAt(i) >= 48 && str.charCodeAt(i) <= 57) ||
          ((str.charAt(i) === "+" ||
            str.charAt(i) === "-" ||
            str.charAt(i) === "/" ||
            str.charAt(i) === " ") &&
            isPhone)
        )
      ) {
        return false;
      }
    }

    return true;
  }

  function submitForm() {
    alert("errorName: " + errorName);
    alert("!checkNameSurname(name): " + !checkNameSurname(name));

    if (
      errorName ||
      errorSurname ||
      errorPhoneNo ||
      errorEmail ||
      errorQuantity ||
      errorTyreWidth ||
      errorTyreHeight ||
      errorTyreDiameter ||
      errorTermsConditions
    ) {
      alert("Nevaljan unos!");
      return;
    }

    Axios.post("http://localhost:3001/api/insertAppointment", {
      buyer_name: name,
      buyer_surname: surname,
      offer_code: offerCode,
      quantity: quantity,
      e_mail: Email,
      phone_no: phoneNo,
      comments: comment,
      tyre_dimension: tyreWidth + "/" + tyreHeight + "/" + tyreDiameter,
    });
  }

  return (
    <form className="appointment">
      <div className="form--container">
        <p className="title">Osobni podaci</p>
        <TextField
          error={errorName}
          id="name"
          variant="outlined"
          label="Ime"
          placeholder="Ivan"
          required
          className="container--item"
          helperText={errorName ? "Unesite ime." : ""}
          size="small"
          onLoad={(event) => {
            setName(event.target.value);
          }}
          onChange={(event) => {
            setName(event.target.value);
          }}
        />
        <TextField
          error={errorSurname}
          variant="outlined"
          id="surname"
          label="Prezime"
          placeholder="Horvat"
          required
          className="container--item"
          helperText={errorSurname ? "Unesite prezime." : ""}
          size="small"
          onChange={(event) => {
            setSurname(event.target.value);
          }}
        />
        <TextField
          error={errorEmail}
          variant="outlined"
          id="e_mail"
          label="Adresa e-pošte"
          placeholder="ivan.horvat@mail.com"
          required
          className="container--item"
          helperText={errorEmail ? "Unesite valjanu adresu e-pošte." : ""}
          size="small"
          onChange={(event) => {
            setEmail(event.target.value);
          }}
        />
        <TextField
          error={errorPhoneNo}
          variant="outlined"
          id="phone_no"
          label="Telefon/mobitel"
          placeholder="0912345678"
          required
          className="container--item"
          helperText={errorPhoneNo ? "Unesite telefon/mobitel." : ""}
          size="small"
          onChange={(event) => {
            setPhoneNo(event.target.value);
          }}
        />
      </div>

      <div className="form--container">
        <p className="title">Podaci vezani za narudžbu</p>
        <TextField
          error={errorOfferCode}
          variant="outlined"
          id="offer_code"
          label="Šifra proizvoda"
          placeholder="1234567"
          required
          className="container--item"
          helperText={errorOfferCode ? "Unesite šifru proizvoda." : ""}
          size="small"
          onChange={(event) => {
            setOfferCode(event.target.value);
          }}
        />
        <TextField
          error={errorQuantity}
          variant="outlined"
          id="quantity"
          label="Količina"
          placeholder="4"
          required
          className="container--item"
          helperText={errorQuantity ? "Unesite količinu." : ""}
          size="small"
          onChange={(event) => {
            setQuantity(event.target.value);
          }}
        />

        <div className="container--item--tyre--dimensions">
          <FormControl
            variant="outlined"
            className="dimensions--item"
            size="small"
          >
            <InputLabel id="tyre-width-select-label">Širina gume</InputLabel>
            <Select
              labelId="tyre-width-select-label"
              id="tyre-width-select"
              onChange={(event) => {
                setTyreWidth(event.target.value);
              }}
              label="Širina gume"
              value={tyreWidth === 0 ? "" : tyreWidth}
              error={errorTyreWidth}
              // helperText={errorTyreWidth ? "Unesite širinu gume." : ""}
            >
              <MenuItem value={135}>135</MenuItem>
              <MenuItem value={145}>145</MenuItem>
              <MenuItem value={155}>155</MenuItem>
              <MenuItem value={165}>165</MenuItem>
              <MenuItem value={175}>175</MenuItem>
              <MenuItem value={185}>185</MenuItem>
              <MenuItem value={195}>195</MenuItem>
              <MenuItem value={205}>205</MenuItem>
              <MenuItem value={215}>215</MenuItem>
              <MenuItem value={225}>225</MenuItem>
              <MenuItem value={235}>235</MenuItem>
              <MenuItem value={245}>245</MenuItem>
              <MenuItem value={255}>255</MenuItem>
              <MenuItem value={265}>265</MenuItem>
              <MenuItem value={275}>275</MenuItem>
              <MenuItem value={285}>285</MenuItem>
              <MenuItem value={295}>295</MenuItem>
            </Select>
          </FormControl>

          <FormControl
            variant="outlined"
            className="dimensions--item"
            size="small"
          >
            <InputLabel id="tyre-height-select-label">Visina gume</InputLabel>
            <Select
              labelId="tyre-height-select-label"
              id="tyre-height-select"
              label="Visina gume"
              value={tyreHeight === 0 ? "" : tyreHeight}
              error={errorTyreHeight}
              // helperText={errorTyreHeight ? "Unesite visinu gume." : ""}
              onChange={(event) => {
                setTyreHeight(event.target.value);
              }}
            >
              <MenuItem value={25}>25</MenuItem>
              <MenuItem value={30}>30</MenuItem>
              <MenuItem value={35}>35</MenuItem>
              <MenuItem value={40}>40</MenuItem>
              <MenuItem value={45}>45</MenuItem>
              <MenuItem value={50}>50</MenuItem>
              <MenuItem value={55}>55</MenuItem>
              <MenuItem value={60}>60</MenuItem>
              <MenuItem value={65}>65</MenuItem>
              <MenuItem value={70}>70</MenuItem>
              <MenuItem value={75}>75</MenuItem>
              <MenuItem value={80}>80</MenuItem>
            </Select>
          </FormControl>

          <FormControl
            variant="outlined"
            className="dimensions--item"
            size="small"
          >
            <InputLabel id="tyre-diameter-select-label">
              Promjer gume (")
            </InputLabel>
            <Select
              labelId="tyre-diameter-select-label"
              id="tyre-diameter-select"
              value={tyreDiameter === 0 ? "" : tyreDiameter}
              onChange={(event) => {
                setTyreDiameter(event.target.value);
              }}
              label={'Promjer gume (")'}
              error={errorTyreDiameter}
              // helperText={errorTyreDiameter ? "Unesite visinu gume." : ""}
            >
              <MenuItem value={11}>11</MenuItem>
              <MenuItem value={12}>12</MenuItem>
              <MenuItem value={13}>13</MenuItem>
              <MenuItem value={14}>14</MenuItem>
              <MenuItem value={15}>15</MenuItem>
              <MenuItem value={16}>16</MenuItem>
              <MenuItem value={17}>17</MenuItem>
              <MenuItem value={18}>18</MenuItem>
              <MenuItem value={19}>19</MenuItem>
              <MenuItem value={20}>20</MenuItem>
              <MenuItem value={21}>21</MenuItem>
              <MenuItem value={22}>22</MenuItem>
            </Select>
          </FormControl>
        </div>

        <TextareaAutosize
          aria-label="Dodatni komentar"
          id="comment"
          rowsMin={5}
          placeholder="  Komentar..."
          className="comment--textarea"
          onChange={(event) => {
            setComment(event.target.value);
          }}
        />
      </div>

      <div className="form--container">
        <FormControl
          required
          error={errorTermsConditions}
          className="container--item"
        >
          <FormGroup>
            <FormControlLabel
              control={
                <Checkbox
                  onChange={(event) => {
                    setErrorTermsConditions(!event.target.checked);
                  }}
                  name="checkTermsConditions"
                  id="checkTermsConditions"
                />
              }
              label="Slažem se s Odredbama i uvjetima te Pravilima privatnosti"
            />
          </FormGroup>
          {errorTermsConditions ? (
            <FormHelperText>Za nastavak je nužno prihvatiti.</FormHelperText>
          ) : (
            " "
          )}
        </FormControl>

        <Button
          className="submit--button"
          linkon="0"
          buttonsize="btn--large"
          buttonstyle="btn--primary"
          onClick={() => {
            let n = name;
            let s = surname;
            let p = phoneNo;
            let q = quantity;
            let tw = tyreWidth;
            let th = setTyreHeight;
            let td = tyreDiameter;

            setErrorName(!checkNameSurname(n));
            setErrorSurname(!checkNameSurname(s));
            setErrorPhoneNo(!checkPhoneQuantity(p, true));
            setErrorQuantity(!checkPhoneQuantity(q, false));
            setErrorTyreWidth(tw === 0);
            setErrorTyreHeight(th === 0);
            setErrorTyreDiameter(td === 0);

            const regexMail = /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
            setErrorEmail(!regexMail.test(String(Email).toLowerCase()));

            submitForm(
              errorName,
              errorSurname,
              errorPhoneNo,
              errorQuantity,
              errorTyreWidth,
              errorTyreHeight,
              errorTyreDiameter
            );
          }}
        >
          NARUČI SE
        </Button>
      </div>
    </form>
  );
}
