import "../../App.css";
import React, { useState, useEffect } from "react";
import Axios from "axios";
import { makeStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TablePagination from "@material-ui/core/TablePagination";
import TableRow from "@material-ui/core/TableRow";
import { Button } from "@material-ui/core";

const columns = [
  { id: "idorder", label: "ID", minWidth: 15 },
  { id: "buyer_name", label: "Ime", minWidth: 100 },
  { id: "buyer_surname", label: "Prezime", minWidth: 100 },
  { id: "offer_code", label: "Kod", minWidth: 15 },
  { id: "quantity", label: "Količina", minWidth: 25 },
  { id: "e_mail", label: "E-mail", minWidth: 100 },
  { id: "phone_no", label: "Mobitel", minWidth: 100 },
  { id: "comments", label: "Komentar", minWidth: 100 },
];

const useStyles = makeStyles({
  root: {
    width: "75%",
    alignContent: "center",
  },
  container: {
    maxHeight: 440,
  },
});

function AppointmentManager() {
  const classes = useStyles();
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(+event.target.value);
    setPage(0);
  };

  const [appointmentList, setAppointmentList] = useState([]);

  useEffect(() => {
    Axios.get("http://localhost:3001/api/getAppointments").then((response) => {
      setAppointmentList(response.data);
    });
  }, []);

  return (
    <Paper className={classes.root}>
      <TableContainer className={classes.container}>
        <Table stickyHeader aria-label="sticky table">
          <TableHead>
            <TableRow>
              {columns.map((column) => (
                <TableCell
                  key={column.id}
                  align={column.align}
                  style={{ minWidth: column.minWidth }}
                >
                  {column.label}
                </TableCell>
              ))}

              <TableCell>Izmjena termina</TableCell>
              <TableCell>Brisanje termina</TableCell>
              <TableCell>Potvrda termina</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {appointmentList
              .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
              .map((row) => {
                return (
                  <TableRow hover role="checkbox" tabIndex={-1} key={row.code}>
                    {columns.map((column) => {
                      const value = row[column.id];
                      return (
                        <TableCell key={column.id} align={column.align}>
                          {column.format && typeof value === "number"
                            ? column.format(value)
                            : value}
                        </TableCell>
                      );
                    })}
                  </TableRow>
                );
              })}

            <TableCell key="edit_appointment" align="right">
              <Button linkon="0" buttonStyle="btn--primary">
                Izmjeni termin
              </Button>
            </TableCell>
          </TableBody>
        </Table>
      </TableContainer>

      <TablePagination
        rowsPerPageOptions={[10, 25, 100]}
        component="div"
        count={appointmentList.length}
        rowsPerPage={rowsPerPage}
        page={page}
        onChangePage={handleChangePage}
        onChangeRowsPerPage={handleChangeRowsPerPage}
      />
    </Paper>
  );
}

export default AppointmentManager;
